
var credit = 10;function InitCredit(){var divCredits=document.getElementById('divCredits');for(var i=0;i<10;i++){var img=document.createElement('img');img.id='id'+i;img.border=0;img.src='pacman/credits/'+i+'.jpg';img.style.visibility='visible';divCredits.appendChild(img)}credit=new Credit();credit.SetTotalCoins(0)};function Credit(){this.divCredits=document.getElementById('divCredits');this.dummy=0};Credit.prototype.Clean=function(){this.divCredits.innerHTML=''};Credit.prototype.AddNumber=function(img){this.divCredits.appendChild(img)};Credit.prototype.LoadImageNumber=function(number){var img=document.createElement('img');img.id='digit'+number;img.border=0;img.src='pacman/credits/'+number+'.jpg';img.style.visibility='visible';return img};Credit.prototype.SetTotalCoins=function(value){this.Clean();var number=value.toString();var size=number.length;for(var i=0;i<size;i++){var digit=number.substr(i,1);var img=this.LoadImageNumber(digit);this.AddNumber(img)}};