
var message;function InitMessage(){var divMessages=document.getElementById('divMessages');this.messages=new Array();this.messages=['gameover','gameoverplayer1','ready','readyplayer1'];for(var i=0;i<this.messages.length;i++){var img=document.createElement('img');img.id='id'+i;img.border=0;img.src='pacman/messages/'+this.messages[i]+'.gif';img.style.visibility='hidden';divMessages.appendChild(img)}message=new Message(this.messages)};function Message(messages){this.messages=messages;this.divMessages=document.getElementById('divMessages');this.dummy=0;this.currentMessage=''};Message.prototype.AddMessage=function(img){this.divMessages.appendChild(img)};Message.prototype.LoadImageMessage=function(message){var img=document.createElement('img');img.id='message'+message;img.border=0;img.src='pacman/messages/'+message+'.gif';img.style.visibility='visible';return img};Message.prototype.ShowMessage=function(message){this.CleanMessage();for(var i=0;i<this.messages.length;i++){if(this.messages[i]==message){var img=this.LoadImageMessage(message);this.AddMessage(img);break}}};Message.prototype.CleanMessage=function(){this.divMessages.innerHTML=''};